<script lang="ts">
    import { onMount } from "svelte";
    const { class:classname }: { class: string } = $props()

    let contador = $state(1);
    const total = $state(4);
    const intervalo = $state(1000); // 2 segundos

    // Atualiza o contador a cada 2 segundos
    onMount(() => {
        const timer = setInterval(() => {
        contador = contador % total + 1; // volta pra 1 depois do 4
        }, intervalo);

        return () => clearInterval(timer);
    });

    // Função para calcular a classe de cada componente
    function getClasse(base:string, offset:number) {
        // desloca o contador pra cada componente
        let numero = ((contador - 1 + offset) % total) + 1;
        console.log(numero)
        return `${base}${numero}`;
    }

</script>

